import React, { useState } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  const [powerHp, setPowerHp] = useState('');
  const [weightLb, setWeightLb] = useState('');
  const [tireSize, setTireSize] = useState('');
  const [nitrousShot, setNitrousShot] = useState('');
  const [engineBuild, setEngineBuild] = useState(1);
  const [result, setResult] = useState(null);
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');

    try {
      const response = await axios.post('http://localhost:5000/calculate', {
        power_hp: parseFloat(powerHp),
        weight_lb: parseFloat(weightLb),
        tire_size: parseFloat(tireSize),
        nitrous_shot: parseFloat(nitrousShot),
        engine_build: engineBuild,
      });

      setResult(response.data.quarter_mile_time);
    } catch (err) {
      setError('Error calculating quarter-mile time. Please try again.');
    }
  };

  return (
    <div className="App">
      <h1>Drag Race Performance Calculator</h1>
      <form onSubmit={handleSubmit}>
        <div>
          <label>
            Engine Power (HP):
            <input
              type="number"
              value={powerHp}
              onChange={(e) => setPowerHp(e.target.value)}
              required
            />
          </label>
        </div>
        <div>
          <label>
            Car Weight (LB):
            <input
              type="number"
              value={weightLb}
              onChange={(e) => setWeightLb(e.target.value)}
              required
            />
          </label>
        </div>
        <div>
          <label>
            Tire Size (Inches):
            <input
              type="number"
              value={tireSize}
              onChange={(e) => setTireSize(e.target.value)}
              required
            />
          </label>
        </div>
        <div>
          <label>
            Nitrous Shot (HP):
            <input
              type="number"
              value={nitrousShot}
              onChange={(e) => setNitrousShot(e.target.value)}
              required
            />
          </label>
        </div>
        <div>
          <label>
            Engine Build:
            <select value={engineBuild} onChange={(e) => setEngineBuild(e.target.value)}>
              <option value={1}>Stock</option>
              <option value={2}>Mildly Built</option>
              <option value={3}>Fully Built</option>
            </select>
          </label>
        </div>
        <button type="submit">Calculate</button>
      </form>

      {error && <p style={{ color: 'red' }}>{error}</p>}
      {result !== null && <h2>Estimated quarter-mile time: {result.toFixed(2)} seconds</h2>}
    </div>
  );
}

export default App;
